function extractRssItems(xml, category, maxItems = 5) {
  if (!xml || typeof xml !== 'string') return [];
  const items = [];
  const regex = /<item>(.*?)<\/item>/gs;
  let match;
  while ((match = regex.exec(xml)) !== null && items.length < maxItems) {
    const titleMatch = match[1].match(/<title>(?:<!\[CDATA\[)?(.*?)(?:\]\]>)?<\/title>/);
    const linkMatch = match[1].match(/<link>(.*?)<\/link>/);
    const descMatch = match[1].match(/<description>(?:<!\[CDATA\[)?(.*?)(?:\]\]>)?<\/description>/s);
    if (titleMatch) {
      const descHtml = descMatch ? descMatch[1] : '';
      const descLinkMatch = descHtml.match(/href="([^"]+)"/);
      const link = linkMatch ? linkMatch[1].trim() : (descLinkMatch ? descLinkMatch[1] : '');
      items.push({
        category,
        title: titleMatch[1].replace(/<[^>]*>/g, '').trim(),
        description: descHtml.replace(/<[^>]*>/g, '').substring(0, 200).trim(),
        link
      });
    }
  }
  return items;
}

function extractAtomEntries(xml, sourceName, maxItems = 5, hoursAgo = 72) {
  if (!xml || typeof xml !== 'string') return [];
  const entries = [];
  const cutoff = new Date(Date.now() - hoursAgo * 60 * 60 * 1000);
  const regex = /<entry>(.*?)<\/entry>/gs;
  let match;
  while ((match = regex.exec(xml)) !== null && entries.length < maxItems) {
    const titleMatch = match[1].match(/<title>(.*?)<\/title>/);
    const pubMatch = match[1].match(/<published>(.*?)<\/published>/);
    const descMatch = match[1].match(/<media:description>(.*?)<\/media:description>/s);
    const linkMatch = match[1].match(/<link[^>]+href="([^"]+)"/);
    if (titleMatch) {
      const pubDate = pubMatch ? new Date(pubMatch[1]) : new Date(0);
      if (pubDate >= cutoff) {
        entries.push({
          source: sourceName,
          title: titleMatch[1].trim(),
          description: descMatch ? descMatch[1].replace(/<[^>]*>/g, '').substring(0, 200).trim() : '',
          published: pubMatch ? pubMatch[1] : '',
          link: linkMatch ? linkMatch[1] : ''
        });
      }
    }
  }
  return entries;
}

