const items = $input.all();
const habits = {};

for (const item of items) {
  const props = item.json.properties;
  let dateStr = '';

  // Try different possible date field names
  const dateField = props['日期'] || props['Date'] || props['date'];
  if (dateField && dateField.title && dateField.title[0]) {
    dateStr = dateField.title[0].plain_text;
  }

  if (!dateStr || !dateStr.match(/^\d{4}-\d{2}-\d{2}$/)) {
    continue;
  }

  habits[dateStr] = {
    Trading: props['Trading']?.checkbox || false,
    Advertise: props['Advertise']?.checkbox || false,
    Deliever: props['Deliever']?.checkbox || false,
    Deliver: props['Deliver']?.checkbox || false,
    Gym: props['Gym']?.checkbox || false,
    FatLoss: props['FatLoss']?.checkbox || props['Fat Loss']?.checkbox || false,
    AI: props['AI']?.checkbox || false
  };
}

return [{ json: { habits } }];
